import { useEffect, useState } from 'react'


function useDebounce<T>(callback: T, delay?: number): T {
    const [debouncedValue, setDebouncedValue] = useState<T>(callback)

    useEffect(() => {
        const timer = setTimeout(() => setDebouncedValue(callback), delay || 500)

        return () => {
            clearTimeout(timer)
        }
    }, [callback, delay])

    return debouncedValue
}

export default useDebounce
